import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertNewsArticleSchema, 

  insertJobOpeningSchema, 
  insertContactMessageSchema,
  insertCompanyTimelineSchema,
  insertBusinessDivisionSchema,
  insertWheelBrandSchema,
  insertWheelModelSchema
} from "@shared/schema";
import path from "path";
import fs from "fs/promises";
import { ObjectStorageService, ObjectNotFoundError } from "./objectStorage";

// 스펙 데이터 파일 경로
const WHEEL_SPECS_FILE = path.join(process.cwd(), 'data', 'wheelSpecs.json');

// 스펙 데이터 로드 함수
async function loadWheelSpecs() {
  try {
    const data = await fs.readFile(WHEEL_SPECS_FILE, 'utf-8');
    return JSON.parse(data);
  } catch (error) {
    // 파일이 없으면 기본 데이터 사용
    const { wheelSpecsData } = await import('../client/src/lib/wheelSpecsData');
    return wheelSpecsData;
  }
}

// 스펙 데이터 저장 함수
async function saveWheelSpecs(specs: any[]) {
  try {
    await fs.mkdir(path.dirname(WHEEL_SPECS_FILE), { recursive: true });
    await fs.writeFile(WHEEL_SPECS_FILE, JSON.stringify(specs, null, 2));
  } catch (error) {
    console.error('Failed to save wheel specs:', error);
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Auto Admin Dashboard Route - 자동차 관리자 페이지
  app.get("/autoadmin", (req, res) => {
    res.sendFile(path.join(process.cwd(), "flask_server/templates/admin/dashboard.html"));
  });

  // 기존 자동차 페이지는 그대로 유지 - Vite가 처리하도록 제거
  // app.get("/jp/auto", (req, res) => {
  //   res.sendFile(path.join(process.cwd(), "client/index.html"));
  // });

  // Admin API Routes for Dashboard Data
  app.get("/api/admin/dashboard", async (req, res) => {
    try {
      const [newsCount, jobsCount, wheelsCount, brandsCount] = await Promise.all([
        storage.getNewsArticles().then(articles => articles.length),
        storage.getJobOpenings().then(jobs => jobs.length),
        storage.getWheelModels().then(models => models.length),
        storage.getWheelBrands().then(brands => brands.length)
      ]);

      res.json({
        stats: {
          news: newsCount,
          jobs: jobsCount,
          wheels: wheelsCount,
          brands: brandsCount
        }
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch dashboard data" });
    }
  });

  // News Articles Routes
  app.get("/api/news", async (req, res) => {
    try {
      // 임시로 메모리 데이터와 저장된 데이터를 합쳐서 반환
      const { newsData } = await import('../shared/newsData');
      const memoryArticles = storage.getAllNewsArticles();

      // 메모리에 있는 새로운 기사들과 기본 데이터를 합침
      const allArticles = [...newsData, ...memoryArticles];

      res.json(allArticles);
    } catch (error) {
      console.error('Error fetching news articles:', error);
      res.status(500).json({ message: "Failed to fetch news articles" });
    }
  });

  app.get("/api/news/:id", async (req, res) => {
    try {
      const { lang = 'jp' } = req.query;
      const article = await storage.getNewsArticleById(parseInt(req.params.id));
      if (!article) {
        return res.status(404).json({ message: "Article not found" });
      }
      res.json(article);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch article" });
    }
  });

  app.post("/api/news", async (req, res) => {
    try {
      const result = insertNewsArticleSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ message: "Invalid article data" });
      }

      const newArticle = await storage.createNewsArticle(result.data);

      console.log('New article created:', newArticle);
      res.status(201).json(newArticle);
    } catch (error) {
      console.error('Error creating news article:', error);
      res.status(400).json({ message: "Invalid article data" });
    }
  });

  app.put("/api/news/:id", async (req, res) => {
    try {
      const { newsData } = await import('../shared/newsData');
      const articleIndex = newsData.findIndex(item => item.id === parseInt(req.params.id));

      if (articleIndex === -1) {
        return res.status(404).json({ message: "News article not found" });
      }

      const updatedArticle = {
        ...newsData[articleIndex],
        ...req.body,
        id: parseInt(req.params.id),
        updatedAt: new Date().toISOString(),
        publishedAt: req.body.published ? (newsData[articleIndex].publishedAt || new Date().toISOString()) : null,
        // Preserve imageUrl if it exists in original article and not explicitly changed
        imageUrl: req.body.imageUrl !== undefined ? req.body.imageUrl : newsData[articleIndex].imageUrl
      };

      // 실제로는 데이터베이스에 업데이트해야 하지만, 임시로 성공 응답만 반환
      console.log('Article updated:', updatedArticle);
      res.json(updatedArticle);
    } catch (error) {
      console.error('Error updating news article:', error);
      res.status(400).json({ message: "Invalid article data" });
    }
  });

  app.delete("/api/news/:id", async (req, res) => {
    try {
      const { newsData } = await import('../shared/newsData');
      const articleIndex = newsData.findIndex(item => item.id === parseInt(req.params.id));

      if (articleIndex === -1) {
        return res.status(404).json({ message: "News article not found" });
      }

      // 실제로는 데이터베이스에서 삭제해야 하지만, 임시로 성공 응답만 반환
      console.log('Article deleted:', req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error('Error deleting news article:', error);
      res.status(500).json({ message: "Failed to delete news article" });
    }
  });

  // Object Storage Routes
  app.get("/objects/:objectPath(*)", async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const objectFile = await objectStorageService.getObjectEntityFile(
        req.path,
      );
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error checking object access:", error);
      if (error instanceof ObjectNotFoundError) {
        return res.sendStatus(404);
      }
      return res.sendStatus(500);
    }
  });

  app.post("/api/objects/upload", async (req, res) => {
    try {
      const objectStorageService = new ObjectStorageService();
      const { filename } = req.body;
      const uploadURL = await objectStorageService.getObjectEntityUploadURL(filename);
      res.json({ uploadURL });
    } catch (error) {
      console.error('Error getting upload URL:', error);
      res.status(500).json({ error: "Failed to get upload URL" });
    }
  });



  // Job Openings Routes
  app.get("/api/jobs", async (req, res) => {
    try {
      const { lang = 'jp', department, location, type } = req.query;
      const jobs = await storage.getJobOpenings({
        language: lang as string,
        department: department as string,
        location: location as string,
        type: type as string
      });
      res.json(jobs);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch job openings" });
    }
  });

  app.get("/api/jobs/:id", async (req, res) => {
    try {
      const { lang = 'jp' } = req.query;
      const job = await storage.getJobOpeningById(parseInt(req.params.id), lang as string);
      if (!job) {
        return res.status(404).json({ message: "Job opening not found" });
      }
      res.json(job);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch job opening" });
    }
  });

  app.post("/api/jobs", async (req, res) => {
    try {
      const validatedData = insertJobOpeningSchema.parse(req.body);
      const job = await storage.createJobOpening(validatedData);
      res.status(201).json(job);
    } catch (error) {
      res.status(400).json({ message: "Invalid job data" });
    }
  });

  // Contact Messages Routes
  app.post("/api/contact", async (req, res) => {
    try {
      const validatedData = insertContactMessageSchema.parse(req.body);
      const message = await storage.createContactMessage(validatedData);
      res.status(201).json({ message: "Contact message sent successfully" });
    } catch (error) {
      res.status(400).json({ message: "Invalid contact data" });
    }
  });

  app.get("/api/contact", async (req, res) => {
    try {
      const { status } = req.query;
      const messages = await storage.getContactMessages(status as string);
      res.json(messages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch contact messages" });
    }
  });

  // Company Timeline Routes
  app.get("/api/timeline", async (req, res) => {
    try {
      const { lang = 'jp' } = req.query;
      const timeline = await storage.getCompanyTimeline(lang as string);
      res.json(timeline);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch company timeline" });
    }
  });

  app.post("/api/timeline", async (req, res) => {
    try {
      const validatedData = insertCompanyTimelineSchema.parse(req.body);
      const event = await storage.createTimelineEvent(validatedData);
      res.status(201).json(event);
    } catch (error) {
      res.status(400).json({ message: "Invalid timeline data" });
    }
  });

  // Business Divisions Routes
  app.get("/api/divisions", async (req, res) => {
    try {
      const { lang = 'jp' } = req.query;
      const divisions = await storage.getBusinessDivisions();
      res.json(divisions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch business divisions" });
    }
  });

  app.post("/api/divisions", async (req, res) => {
    try {
      const validatedData = insertBusinessDivisionSchema.parse(req.body);
      const division = await storage.createBusinessDivision(validatedData);
      res.status(201).json(division);
    } catch (error) {
      res.status(400).json({ message: "Invalid division data" });
    }
  });

  // Statistics endpoint
  app.get("/api/stats", async (req, res) => {
    try {
      const stats = await storage.getWebsiteStats();
      res.json(stats);
    } catch (error) {
      // 임시로 기본값 반환
      res.json({
        totalNews: 0,
        totalJobs: 0,
        totalMessages: 0
      });
    }
  });

  // Wheel Brands Routes
  app.get("/api/wheels/brands", async (req, res) => {
    try {
      // 임시로 wheelData.ts의 데이터 사용
      const { wheelBrands } = await import('../client/src/lib/wheelData');
      const { active } = req.query;

      let brands = wheelBrands;
      if (active !== undefined) {
        brands = brands.filter(brand => brand.active === (active === 'true'));
      }

      res.json(brands);
    } catch (error) {
      console.error('Error fetching wheel brands:', error);
      res.status(500).json({ message: "Failed to fetch wheel brands" });
    }
  });

  app.get("/api/wheels/brands/:id", async (req, res) => {
    try {
      const brand = await storage.getWheelBrandById(parseInt(req.params.id));
      if (!brand) {
        return res.status(404).json({ message: "Wheel brand not found" });
      }
      res.json(brand);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch wheel brand" });
    }
  });

  app.post("/api/wheels/brands", async (req, res) => {
    try {
      // 임시로 메모리에 저장 (실제로는 데이터베이스에 저장해야 함)
      const { wheelBrands } = await import('../client/src/lib/wheelData');

      const newBrand = {
        id: Math.max(...wheelBrands.map(b => b.id)) + 1,
        name: req.body.name,
        nameJp: req.body.nameJp,
        description: req.body.description,
        active: req.body.active !== undefined ? req.body.active : true,
        sortOrder: req.body.sortOrder || 0
      };

      // 실제로는 데이터베이스에 저장해야 하지만, 임시로 성공 응답만 반환
      console.log('New brand created:', newBrand);
      res.status(201).json(newBrand);
    } catch (error) {
      console.error('Error creating wheel brand:', error);
      res.status(400).json({ message: "Invalid wheel brand data" });
    }
  });

  app.put("/api/wheels/brands/:id", async (req, res) => {
    try {
      const validatedData = insertWheelBrandSchema.partial().parse(req.body);
      const brand = await storage.updateWheelBrand(parseInt(req.params.id), validatedData);
      res.json(brand);
    } catch (error) {
      res.status(400).json({ message: "Invalid wheel brand data" });
    }
  });

  app.delete("/api/wheels/brands/:id", async (req, res) => {
    try {
      await storage.deleteWheelBrand(parseInt(req.params.id));
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete wheel brand" });
    }
  });

  // Wheel Models Routes
  app.get("/api/wheels/models", async (req, res) => {
    try {
      // 임시로 wheelData.ts의 데이터 사용
      const { wheelModels } = await import('../client/src/lib/wheelData');
      const { brandId, status } = req.query;

      let models = wheelModels;

      if (brandId) {
        models = models.filter(model => model.brandId === parseInt(brandId as string));
      }

      if (status) {
        models = models.filter(model => model.status === status);
      }

      res.json(models);
    } catch (error) {
      console.error('Error fetching wheel models:', error);
      res.status(500).json({ message: "Failed to fetch wheel models" });
    }
  });

  app.get("/api/wheels/models/:id", async (req, res) => {
    try {
      const model = await storage.getWheelModelById(parseInt(req.params.id));
      if (!model) {
        return res.status(404).json({ message: "Wheel model not found" });
      }
      res.json(model);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch wheel model" });
    }
  });

  app.post("/api/wheels/models", async (req, res) => {
    try {
      // 임시로 메모리에 저장
      const { wheelModels } = await import('../client/src/lib/wheelData');

      const newModel = {
        id: Math.max(...wheelModels.map(m => m.id)) + 1,
        brandId: req.body.brandId,
        name: req.body.name,
        nameJp: req.body.nameJp || req.body.name,
        imageUrl: req.body.imageUrl,
        specs: req.body.specs,
        description: req.body.description || { jp: '', ko: '', en: '', zh: '' },
        status: req.body.status || 'active',
        sortOrder: req.body.sortOrder || 0
      };

      console.log('New model created:', newModel);
      res.status(201).json(newModel);
    } catch (error) {
      console.error('Error creating wheel model:', error);
      res.status(400).json({ message: "Invalid wheel model data" });
    }
  });

  app.put("/api/wheels/models/:id", async (req, res) => {
    try {
      const validatedData = insertWheelModelSchema.partial().parse(req.body);
      const model = await storage.updateWheelModel(parseInt(req.params.id), validatedData);
      res.json(model);
    } catch (error) {
      res.status(400).json({ message: "Invalid wheel model data" });
    }
  });

  app.delete("/api/wheels/models/:id", async (req, res) => {
    try {
      await storage.deleteWheelModel(parseInt(req.params.id));
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete wheel model" });
    }
  });

  // Wheel Specs Routes
  app.get("/api/wheels/specs", async (req, res) => {
    try {
      console.log('GET /api/wheels/specs called');
      // 임시로 wheelSpecsData.ts의 데이터 사용
      const wheelSpecsData = await loadWheelSpecs();
      const { brandId, modelId } = req.query;

      console.log('Query params:', { brandId, modelId });
      console.log('Total specs in data:', wheelSpecsData.length);

      let specs = wheelSpecsData;

      if (brandId) {
        specs = specs.filter((spec: any) => spec.brandId === parseInt(brandId as string));
        console.log('Filtered by brandId:', brandId, 'Result count:', specs.length);
      }

      if (modelId) {
        specs = specs.filter((spec: any) => spec.modelId === parseInt(modelId as string));
        console.log('Filtered by modelId:', modelId, 'Result count:', specs.length);
      }

      console.log('Returning specs count:', specs.length);
      res.json(specs);
    } catch (error) {
      console.error('Error fetching wheel specs:', error);
      res.status(500).json({ message: "Failed to fetch wheel specs" });
    }
  });

  app.post("/api/wheels/specs", async (req, res) => {
    try {
      // 기존 데이터 로드
      const wheelSpecsData = await loadWheelSpecs();

      const newSpec = {
        id: Math.max(...wheelSpecsData.map((s: any) => s.id)) + 1,
        brandId: req.body.brandId,
        modelId: req.body.modelId,
        brandName: req.body.brandName,
        modelName: req.body.modelName,
        size: req.body.size,
        pcd: req.body.pcd,
        holes: req.body.holes,
        inset: req.body.inset,
        discProtrusion: req.body.discProtrusion,
        hubHeight: req.body.hubHeight,
        price: req.body.price,
        color: req.body.color,
        accessories: req.body.accessories,
        weight: req.body.weight
      };

      wheelSpecsData.push(newSpec);

      // 파일에 저장
      await saveWheelSpecs(wheelSpecsData);

      console.log('New spec created:', newSpec);
      res.status(201).json(newSpec);
    } catch (error) {
      console.error('Error creating wheel spec:', error);
      res.status(400).json({ message: "Invalid wheel spec data" });
    }
  });

  app.put("/api/wheels/specs/:id", async (req, res) => {
    try {
      // 기존 데이터 로드
      const wheelSpecsData = await loadWheelSpecs();

      const specId = parseInt(req.params.id);
      const existingSpec = wheelSpecsData.find((s: any) => s.id === specId);

      if (!existingSpec) {
        return res.status(404).json({ message: "Wheel spec not found" });
      }

      const updatedSpec = {
        ...existingSpec,
        brandId: req.body.brandId || existingSpec.brandId,
        modelId: req.body.modelId || existingSpec.modelId,
        brandName: req.body.brandName || existingSpec.brandName,
        modelName: req.body.modelName || existingSpec.modelName,
        size: req.body.size || existingSpec.size,
        pcd: req.body.pcd || existingSpec.pcd,
        holes: req.body.holes || existingSpec.holes,
        inset: req.body.inset || existingSpec.inset,
        discProtrusion: req.body.discProtrusion || existingSpec.discProtrusion,
        hubHeight: req.body.hubHeight || existingSpec.hubHeight,
        price: req.body.price || existingSpec.price,
        color: req.body.color || existingSpec.color,
        accessories: req.body.accessories || existingSpec.accessories,
        weight: req.body.weight || existingSpec.weight
      };

      // 배열에서 기존 항목을 업데이트된 항목으로 교체
      const specIndex = wheelSpecsData.findIndex((s: any) => s.id === specId);
      if (specIndex !== -1) {
        wheelSpecsData[specIndex] = updatedSpec;
      }

      // 파일에 저장
      await saveWheelSpecs(wheelSpecsData);

      console.log('Spec updated:', updatedSpec);
      res.json(updatedSpec);
    } catch (error) {
      console.error('Error updating wheel spec:', error);
      res.status(400).json({ message: "Invalid wheel spec data" });
    }
  });

  app.delete("/api/wheels/specs/:id", async (req, res) => {
    try {
      // 기존 데이터 로드
      const wheelSpecsData = await loadWheelSpecs();

      const specId = parseInt(req.params.id);
      const existingSpec = wheelSpecsData.find((s: any) => s.id === specId);

      if (!existingSpec) {
        return res.status(404).json({ message: "Wheel spec not found" });
      }

      // 배열에서 항목 제거
      const filteredSpecs = wheelSpecsData.filter((s: any) => s.id !== specId);

      // 파일에 저장
      await saveWheelSpecs(filteredSpecs);

      console.log('Spec deleted:', existingSpec);
      res.status(204).send();
    } catch (error) {
      console.error('Error deleting wheel spec:', error);
      res.status(500).json({ message: "Failed to delete wheel spec" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  newsArticles, 
  jobOpenings, 
  contactMessages, 
  companyTimeline,
  businessDivisions,
  wheelBrands,
  wheelModels,
  wheelSpecs,
  type User, 
  type InsertUser,
  type NewsArticle,
  type InsertNewsArticle,
  type JobOpening,
  type InsertJobOpening,
  type ContactMessage,
  type InsertContactMessage,
  type CompanyTimelineEvent,
  type InsertCompanyTimelineEvent,
  type BusinessDivision,
  type InsertBusinessDivision,
  type WheelBrand,
  type InsertWheelBrand,
  type WheelModel,
  type InsertWheelModel,
  type WheelSpec,
  type InsertWheelSpec
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, or, like, count } from "drizzle-orm";

// Enhanced storage interface with all CRUD methods
export interface IStorage {
  // User methods
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // News methods
  getNewsArticles(filters?: { 
    language?: string; 
    category?: string; 
    limit?: number; 
    published?: boolean;
  }): Promise<NewsArticle[]>;
  getAllNewsArticles(): NewsArticle[];
  getNewsArticleById(id: number, language?: string): Promise<NewsArticle | undefined>;
  createNewsArticle(article: InsertNewsArticle): Promise<NewsArticle>;



  // Job methods
  getJobOpenings(filters?: {
    language?: string;
    department?: string;
    location?: string;
    type?: string;
    active?: boolean;
  }): Promise<JobOpening[]>;
  getJobOpeningById(id: number, language?: string): Promise<JobOpening | undefined>;
  createJobOpening(job: InsertJobOpening): Promise<JobOpening>;

  // Contact methods
  createContactMessage(message: InsertContactMessage): Promise<ContactMessage>;
  getContactMessages(status?: string): Promise<ContactMessage[]>;

  // Timeline methods
  getCompanyTimeline(language?: string): Promise<CompanyTimelineEvent[]>;
  createTimelineEvent(event: InsertCompanyTimelineEvent): Promise<CompanyTimelineEvent>;

  // Business division methods
  getBusinessDivisions(language?: string): Promise<BusinessDivision[]>;
  createBusinessDivision(division: InsertBusinessDivision): Promise<BusinessDivision>;

  // Wheel methods
  getWheelBrands(active?: boolean): Promise<WheelBrand[]>;
  getWheelBrandById(id: number): Promise<WheelBrand | undefined>;
  createWheelBrand(brand: InsertWheelBrand): Promise<WheelBrand>;
  updateWheelBrand(id: number, brand: Partial<InsertWheelBrand>): Promise<WheelBrand>;
  deleteWheelBrand(id: number): Promise<void>;

  getWheelModels(brandId?: number, status?: string): Promise<WheelModel[]>;
  getWheelModelById(id: number): Promise<WheelModel | undefined>;
  createWheelModel(model: InsertWheelModel): Promise<WheelModel>;
  updateWheelModel(id: number, model: Partial<InsertWheelModel>): Promise<WheelModel>;
  deleteWheelModel(id: number): Promise<void>;

  getWheelSpecs(modelId?: number, brandId?: number): Promise<WheelSpec[]>;
  getWheelSpecById(id: number): Promise<WheelSpec | undefined>;
  createWheelSpec(spec: InsertWheelSpec): Promise<WheelSpec>;
  updateWheelSpec(id: number, spec: Partial<InsertWheelSpec>): Promise<WheelSpec>;
  deleteWheelSpec(id: number): Promise<void>;

  // Statistics
  getWebsiteStats(): Promise<{
    totalNews: number;
    totalJobs: number;
    totalMessages: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getNewsArticles(filters?: { 
    language?: string; 
    category?: string; 
    limit?: number; 
    published?: boolean;
  }): Promise<NewsArticle[]> {
    const conditions = [];

    if (filters?.published !== undefined) {
      conditions.push(eq(newsArticles.published, filters.published));
    } else {
      conditions.push(eq(newsArticles.published, true));
    }

    if (filters?.category) {
      conditions.push(eq(newsArticles.category, filters.category));
    }

    if (conditions.length > 0 && filters?.limit) {
      return await db.select().from(newsArticles)
        .where(and(...conditions))
        .orderBy(desc(newsArticles.publishedAt))
        .limit(filters.limit);
    } else if (conditions.length > 0) {
      return await db.select().from(newsArticles)
        .where(and(...conditions))
        .orderBy(desc(newsArticles.publishedAt));
    } else if (filters?.limit) {
      return await db.select().from(newsArticles)
        .orderBy(desc(newsArticles.publishedAt))
        .limit(filters.limit);
    }

    return await db.select().from(newsArticles)
      .orderBy(desc(newsArticles.publishedAt));
  }

  async getNewsArticleById(id: number, language?: string): Promise<NewsArticle | undefined> {
    const [article] = await db
      .select()
      .from(newsArticles)
      .where(and(eq(newsArticles.id, id), eq(newsArticles.published, true)));
    return article || undefined;
  }

  async createNewsArticle(article: InsertNewsArticle): Promise<NewsArticle> {
    const [newArticle] = await db
      .insert(newsArticles)
      .values(article)
      .returning();
    return newArticle;
  }



  async getJobOpenings(filters?: {
    language?: string;
    department?: string;
    location?: string;
    type?: string;
    active?: boolean;
  }): Promise<JobOpening[]> {
    const conditions = [];

    if (filters?.active !== undefined) {
      conditions.push(eq(jobOpenings.active, filters.active));
    } else {
      conditions.push(eq(jobOpenings.active, true));
    }

    if (filters?.department) {
      conditions.push(eq(jobOpenings.department, filters.department));
    }

    if (filters?.location) {
      conditions.push(like(jobOpenings.location, `%${filters.location}%`));
    }

    if (filters?.type) {
      conditions.push(eq(jobOpenings.type, filters.type));
    }

    if (conditions.length > 0) {
      return await db.select().from(jobOpenings)
        .where(and(...conditions))
        .orderBy(desc(jobOpenings.createdAt));
    }

    return await db.select().from(jobOpenings)
      .orderBy(desc(jobOpenings.createdAt));
  }

  async getJobOpeningById(id: number, language?: string): Promise<JobOpening | undefined> {
    const [job] = await db
      .select()
      .from(jobOpenings)
      .where(and(eq(jobOpenings.id, id), eq(jobOpenings.active, true)));
    return job || undefined;
  }

  async createJobOpening(job: InsertJobOpening): Promise<JobOpening> {
    const [newJob] = await db
      .insert(jobOpenings)
      .values(job)
      .returning();
    return newJob;
  }

  async createContactMessage(message: InsertContactMessage): Promise<ContactMessage> {
    const [newMessage] = await db
      .insert(contactMessages)
      .values(message)
      .returning();
    return newMessage;
  }

  async getContactMessages(status?: string): Promise<ContactMessage[]> {
    if (status) {
      return await db.select().from(contactMessages)
        .where(eq(contactMessages.status, status))
        .orderBy(desc(contactMessages.createdAt));
    }

    return await db.select().from(contactMessages)
      .orderBy(desc(contactMessages.createdAt));
  }

  async getCompanyTimeline(language?: string): Promise<CompanyTimelineEvent[]> {
    return await db
      .select()
      .from(companyTimeline)
      .orderBy(companyTimeline.sortOrder, companyTimeline.year);
  }

  async createTimelineEvent(event: InsertCompanyTimelineEvent): Promise<CompanyTimelineEvent> {
    const [newEvent] = await db
      .insert(companyTimeline)
      .values(event)
      .returning();
    return newEvent;
  }

  async getBusinessDivisions(language?: string): Promise<BusinessDivision[]> {
    return await db
      .select()
      .from(businessDivisions)
      .orderBy(businessDivisions.sortOrder);
  }

  async createBusinessDivision(division: InsertBusinessDivision): Promise<BusinessDivision> {
    const [newDivision] = await db
      .insert(businessDivisions)
      .values(division)
      .returning();
    return newDivision;
  }

  async getWebsiteStats(): Promise<{
    totalNews: number;
    totalJobs: number;
    totalMessages: number;
  }> {
    const [newsCount] = await db.select({ count: count() }).from(newsArticles);
    const [jobsCount] = await db.select({ count: count() }).from(jobOpenings);
    const [messagesCount] = await db.select({ count: count() }).from(contactMessages);

    return {
      totalNews: newsCount?.count || 0,
      totalJobs: jobsCount?.count || 0,
      totalMessages: messagesCount?.count || 0,
    };
  }

  // Wheel Brand methods
  async getWheelBrands(active?: boolean): Promise<WheelBrand[]> {
    if (active !== undefined) {
      return await db.select().from(wheelBrands)
        .where(eq(wheelBrands.active, active))
        .orderBy(wheelBrands.sortOrder, wheelBrands.name);
    }

    return await db.select().from(wheelBrands)
      .orderBy(wheelBrands.sortOrder, wheelBrands.name);
  }

  async getWheelBrandById(id: number): Promise<WheelBrand | undefined> {
    const [brand] = await db
      .select()
      .from(wheelBrands)
      .where(eq(wheelBrands.id, id));
    return brand || undefined;
  }

  async createWheelBrand(brand: InsertWheelBrand): Promise<WheelBrand> {
    const [newBrand] = await db
      .insert(wheelBrands)
      .values(brand)
      .returning();
    return newBrand;
  }

  async updateWheelBrand(id: number, brand: Partial<InsertWheelBrand>): Promise<WheelBrand> {
    const [updatedBrand] = await db
      .update(wheelBrands)
      .set({ ...brand, updatedAt: new Date() })
      .where(eq(wheelBrands.id, id))
      .returning();
    return updatedBrand;
  }

  async deleteWheelBrand(id: number): Promise<void> {
    await db.delete(wheelBrands).where(eq(wheelBrands.id, id));
  }

  // Wheel Model methods
  async getWheelModels(brandId?: number, status?: string): Promise<WheelModel[]> {
    const conditions = [];

    if (brandId) {
      conditions.push(eq(wheelModels.brandId, brandId));
    }

    if (status) {
      conditions.push(eq(wheelModels.status, status));
    }

    if (conditions.length > 0) {
      return await db.select().from(wheelModels)
        .where(and(...conditions))
        .orderBy(wheelModels.sortOrder, wheelModels.name);
    }

    return await db.select().from(wheelModels)
      .orderBy(wheelModels.sortOrder, wheelModels.name);
  }

  async getWheelModelById(id: number): Promise<WheelModel | undefined> {
    const [model] = await db
      .select()
      .from(wheelModels)
      .where(eq(wheelModels.id, id));
    return model || undefined;
  }

  async createWheelModel(model: InsertWheelModel): Promise<WheelModel> {
    const [newModel] = await db
      .insert(wheelModels)
      .values(model)
      .returning();
    return newModel;
  }

  async updateWheelModel(id: number, model: Partial<InsertWheelModel>): Promise<WheelModel> {
    const [updatedModel] = await db
      .update(wheelModels)
      .set({ ...model, updatedAt: new Date() })
      .where(eq(wheelModels.id, id))
      .returning();
    return updatedModel;
  }

  async deleteWheelModel(id: number): Promise<void> {
    await db.delete(wheelModels).where(eq(wheelModels.id, id));
  }

  // Wheel Spec methods
  async getWheelSpecs(modelId?: number, brandId?: number): Promise<WheelSpec[]> {
    const conditions = [];

    if (modelId) {
      conditions.push(eq(wheelSpecs.modelId, modelId));
    }

    if (brandId) {
      conditions.push(eq(wheelSpecs.brandId, brandId));
    }

    if (conditions.length > 0) {
      return await db.select().from(wheelSpecs)
        .where(and(...conditions))
        .orderBy(wheelSpecs.id);
    }

    return await db.select().from(wheelSpecs).orderBy(wheelSpecs.id);
  }

  async getWheelSpecById(id: number): Promise<WheelSpec | undefined> {
    const [spec] = await db
      .select()
      .from(wheelSpecs)
      .where(eq(wheelSpecs.id, id));
    return spec || undefined;
  }

  async createWheelSpec(spec: InsertWheelSpec): Promise<WheelSpec> {
    const [newSpec] = await db
      .insert(wheelSpecs)
      .values(spec)
      .returning();
    return newSpec;
  }

  async updateWheelSpec(id: number, spec: Partial<InsertWheelSpec>): Promise<WheelSpec> {
    const [updatedSpec] = await db
      .update(wheelSpecs)
      .set({ ...spec, updatedAt: new Date() })
      .where(eq(wheelSpecs.id, id))
      .returning();
    return updatedSpec;
  }

  async deleteWheelSpec(id: number): Promise<void> {
    await db.delete(wheelSpecs).where(eq(wheelSpecs.id, id));
  }

  getAllNewsArticles(): NewsArticle[] {
    // 데이터베이스에서 조회하려 하지만, 연결 실패 시 빈 배열 반환
    return [];
  }
}

// 임시 메모리 스토리지 추가
class MemStorage implements IStorage {
  private newsArticles: NewsArticle[] = [];
  private users: User[] = [];
  private jobOpenings: JobOpening[] = [];
  private contactMessages: ContactMessage[] = [];
  private companyTimeline: CompanyTimelineEvent[] = [];
  private businessDivisions: BusinessDivision[] = [];
  private wheelBrands: WheelBrand[] = [];
