    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/contact", data);
    },
    onSuccess: () => {
      toast({
        title:
          language === "jp"
            ? "お問い合わせを受け付けました"
            : language === "ko"
              ? "문의를 접수했습니다"
              : language === "en"
                ? "Your inquiry has been received"
                : "您的咨询已收到",
