import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { useLanguage } from "@/hooks/useLanguage";
import { getMultiLanguageContent } from "@/lib/i18n";
// formatDate function is defined locally
import { NewsArticle } from "@shared/schema";
import { newsData, newsCategories } from "@/lib/newsData";
import { AnimatedCard } from "@/components/AnimatedCard";
import { useState } from "react";
import { useParams, Link } from "wouter";
import { 
  Calendar, 
  FileText, 
  ArrowLeft,
  Clock
} from "lucide-react";
import { BiSearch, BiShare } from "react-icons/bi";
import { VoiceNarrationButton } from '@/components/VoiceNarrationButton';

export function News() {
  const { language, getLanguageRoute } = useLanguage();
  const params = useParams();
  const articleId = params.id;
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>('all');

  // Use static data instead of API calls during database connection issues
  const article = articleId ? newsData.find(item => item.id === parseInt(articleId)) : null;
  const articleLoading = false;

  const allArticles = newsData.filter(item => 
    selectedCategory === 'all' || item.category === selectedCategory
  );
  const articles = allArticles;
  const articlesLoading = false;

  const categories = newsCategories;

  // Filter articles based on search query
  const filteredArticles = articles?.filter(article => {
    const title = getMultiLanguageContent(article.title, language).toLowerCase();
    const excerpt = getMultiLanguageContent(article.excerpt, language).toLowerCase();
    const query = searchQuery.toLowerCase();
    return title.includes(query) || excerpt.includes(query);
  }) || [];

  const formatDate = (dateString: string | null) => {
    if (!dateString) return '';
    return new Date(dateString).toLocaleDateString(
      language === 'jp' ? 'ja-JP' :
      language === 'ko' ? 'ko-KR' :
      language === 'en' ? 'en-US' :
      'zh-CN'
    );
  };

  // Single article view
  if (articleId) {
    if (articleLoading) {
      return (
        <div className="min-h-screen bg-white">
          <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
            <div className="animate-pulse">
              <div className="h-8 bg-slate-200 rounded w-48 mb-8"></div>
              <div className="h-12 bg-slate-200 rounded mb-4"></div>
              <div className="h-6 bg-slate-200 rounded w-32 mb-8"></div>
              <div className="space-y-4">
                {[...Array(10)].map((_, i) => (
                  <div key={i} className="h-4 bg-slate-200 rounded"></div>
                ))}
              </div>
            </div>
          </div>
        </div>
      );
    }

    if (!article) {
      return (
        <div className="min-h-screen bg-white flex items-center justify-center">
          <Card className="w-full max-w-md mx-4">
            <CardContent className="pt-6 text-center">
              <FileText className="h-16 w-16 text-slate-300 mx-auto mb-4" />
              <h2 className="text-xl font-semibold text-slate-900 mb-2">
                {language === 'jp' && '記事が見つかりません'}
                {language === 'ko' && '기사를 찾을 수 없습니다'}
                {language === 'en' && 'Article not found'}
                {language === 'zh' && '文章未找到'}
              </h2>
              <p className="text-slate-600 mb-4">
                {language === 'jp' && '指定された記事は存在しないか、削除された可能性があります。'}
                {language === 'ko' && '지정된 기사가 존재하지 않거나 삭제되었을 수 있습니다.'}
                {language === 'en' && 'The specified article does not exist or may have been deleted.'}
                {language === 'zh' && '指定的文章不存在或可能已被删除。'}
              </p>
              <Link href={getLanguageRoute('/news')}>
                <Button>
                  {language === 'jp' && 'ニュース一覧に戻る'}
                  {language === 'ko' && '뉴스 목록으로 돌아가기'}
                  {language === 'en' && 'Back to News'}
                  {language === 'zh' && '返回新闻列表'}
                </Button>
              </Link>
            </CardContent>
          </Card>
        </div>
      );
    }

    return (
      <div className="min-h-screen bg-white">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
          {/* Back button */}
          <Button 
            variant="ghost" 
            className="mb-8"
            onClick={() => window.location.href = getLanguageRoute('/news')}
          >
            <ArrowLeft className="mr-2 h-4 w-4" />
            {language === 'jp' && 'ニュース一覧に戻る'}
            {language === 'ko' && '뉴스 목록으로 돌아가기'}
            {language === 'en' && 'Back to News'}
            {language === 'zh' && '返回新闻列表'}
          </Button>

          {/* Article header */}
          <header className="mb-8">
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center gap-2">
                <Badge>{article.category}</Badge>
                {article.publishedAt && (
                  <div className="flex items-center text-sm text-slate-500">
                    <Calendar className="mr-1 h-4 w-4" />
                    {formatDate(article.publishedAt)}
                  </div>
                )}
              </div>
              {/* Voice Narration Button */}
              <VoiceNarrationButton
                text={`${getMultiLanguageContent(article.title, language)}. ${getMultiLanguageContent(article.excerpt, language)}. ${getMultiLanguageContent(article.content, language)}`}
                language={language}
                variant="outline"
                size="sm"
              />
            </div>
            <h1 className="text-3xl md:text-4xl font-bold text-slate-900 mb-6">
              {getMultiLanguageContent(article.title, language)}
            </h1>

            {/* Article Image */}
            {article.imageUrl && (
              <div className="mb-8">
                <img 
                  src={article.imageUrl} 
                  alt={getMultiLanguageContent(article.title, language)}
                  className="w-full h-auto max-h-96 object-cover rounded-lg shadow-lg"
                />
              </div>
            )}
          </header>

          {/* Article content */}
          <div className="prose max-w-none">
            <div className="text-lg text-slate-600 mb-8">
              {getMultiLanguageContent(article.excerpt, language)}
            </div>
            <div 
              className="text-slate-800 leading-relaxed"
              dangerouslySetInnerHTML={{ 
                __html: getMultiLanguageContent(article.content, language) 
              }}
            />
          </div>

          {/* Attached Files */}
          {(article as any).attachedFiles && (article as any).attachedFiles.length > 0 && (
            <div className="mt-8 p-4 bg-gray-50 rounded-lg border">
              <h3 className="text-lg font-semibold mb-4">
                {language === 'jp' && '添付ファイル'}
                {language === 'ko' && '첨부 파일'}
                {language === 'en' && 'Attached Files'}
                {language === 'zh' && '附件'}
              </h3>
              <div className="space-y-2">
                {(article as any).attachedFiles.map((file: any, index: number) => (
                  <a 
                    key={index}
                    href={file.url} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="flex items-center p-3 bg-white rounded-lg border hover:bg-gray-50 transition-colors"
                  >
                    <FileText className="mr-3 h-5 w-5 text-gray-500" />
                    <span className="text-blue-600 hover:text-blue-800 font-medium">
                      {file.name}
                    </span>
                  </a>
                ))}
              </div>
            </div>
          )}

          {/* Share buttons */}
          <div className="mt-12 pt-8 border-t border-slate-200">
            <div className="flex items-center gap-4">
              <span className="text-sm font-medium text-slate-700">
                {language === 'jp' && 'この記事をシェア:'}
                {language === 'ko' && '이 기사 공유:'}
                {language === 'en' && 'Share this article:'}
                {language === 'zh' && '分享这篇文章:'}
              </span>
              <Button variant="outline" size="sm">
                <BiShare className="mr-2 h-4 w-4" />
                {language === 'jp' && 'シェア'}
                {language === 'ko' && '공유'}
                {language === 'en' && 'Share'}
                {language === 'zh' && '분享'}
              </Button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // News listing view
  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="py-16 lg:py-24 corporate-gradient">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <div className="flex justify-between items-start mb-8">
              <div></div>
              <div className="text-center flex-1">
                <h1 className="text-4xl md:text-6xl font-bold text-white mb-6">
                  {language === 'jp' && 'ニュース'}
                  {language === 'ko' && '뉴스'}
                  {language === 'en' && 'News'}
                  {language === 'zh' && '新闻'}
                </h1>
                <p className="text-xl text-white/80 max-w-3xl mx-auto">
                  {language === 'jp' && '最新のニュースやお知らせをご覧ください。'}
                  {language === 'ko' && '최신 뉴스와 공지사항을 확인하세요.'}
                  {language === 'en' && 'Stay updated with our latest news and announcements.'}
                  {language === 'zh' && '查看最新新闻和公告。'}
                </p>
              </div>
              <div className="flex flex-col space-y-2">
                <Link href={getLanguageRoute('/news-admin')}>
                  <Button variant="secondary" size="sm" className="bg-white/10 text-white border-white/20 hover:bg-white/20">
                    {language === 'jp' && '管理者'}
                    {language === 'ko' && '관리자'}
                    {language === 'en' && 'Admin'}
                    {language === 'zh' && '管理员'}
                  </Button>
                </Link>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Search and Filter Section */}
      <section className="py-8 bg-white border-b border-slate-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-6">
            {/* Search */}
            <div className="relative max-w-md">
              <BiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-slate-400" />
              <Input 
                placeholder={
                  language === 'jp' ? 'ニュースを検索...' :
                  language === 'ko' ? '뉴스 검색...' :
                  language === 'en' ? 'Search news...' :
                  '搜索新闻...'
                }
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
              />
            </div>

            {/* Category Filters */}
            <div className="flex flex-wrap gap-2">
              {categories.map((category) => (
                <Button
                  key={category.key}
