  const getPositionIcon = (type: string) => {
    switch (type) {
      case 'executive':
        return <User className="h-5 w-5" />;
      case 'department':
        return <Building2 className="h-4 w-4" />;
      case 'division':
        return <Briefcase className="h-4 w-4" />;
      case 'unit':
        return <Users className="h-4 w-4" />;
      default:
        return <Building2 className="h-4 w-4" />;
